<script lang="ts">
  import { createEventDispatcher, onMount, tick } from 'svelte';
  import { mute } from '..';
  import { _ } from 'svelte-i18n';

  export let id: string;
  export let time = 0;
  export let duration: number;
  export let loadeddata: boolean;
  export let canplay: boolean;
  export let waiting: boolean;
  export let paused: boolean;
  export let buffered;

  const dispatch = createEventDispatcher();

  /**
   * Data images
   */
  const dataimages = {
    play: 'data:image/svg+xml;base64,',
    pause:
      'data:image/svg+xml;base64,',
    mute: 'data:image/svg+xml;base64,',
    loud: 'data:image/svg+xml;base64,'
  };

  let listeners = 0;
  let container: HTMLElement | null;
  let mediaControls: HTMLElement;
  let percentageTime: number;
  let percentageBuffer: number;
  let width: number = 0;
  let height: number = 0;
  let controls: boolean;
  let controlsTimeout: number | undefined;

  $: if (duration) {
    percentageTime = (time * 100) / duration;
    percentageBuffer =
      (buffered?.length && (buffered[buffered.length - 1].end * 100) / duration) || 0;
  }

  onMount(async () => {
    await tick();
    container = document.getElementById(id);
    resize();
    window.addEventListener('resize', resizeHandler);
  });

  function resize() {
    width = container?.clientWidth || 0;
    height = container?.clientHeight || 0;
  }

  function resizeHandler(e: Event) {
    resize();
  }

  function format(seconds: number, prefix = '') {
    if (isNaN(seconds)) return '...';

    const minutes = Math.floor(seconds / 60);
    seconds = Math.floor(seconds % 60);
    const printMinutes = minutes < 10 ? '0' + minutes : minutes;
    const printSeconds = seconds < 10 ? '0' + seconds : seconds;

    return `${prefix}${printMinutes}:${printSeconds}`;
  }

  function display(time: number) {
    if (isNaN(time)) return '...';

    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    const minutesUnit = () => (minutes === 1 ? $_('text.minute') : $_('text.minutes'));
    const secondsUnit = () => (seconds === 1 ? $_('text.second') : $_('text.seconds'));

    return `${minutes} ${minutesUnit()} ${seconds} ${secondsUnit()}`;
  }

  function showControls() {
    controls = true;
    clearTimeout(controlsTimeout);
    controlsTimeout = setTimeout(() => (controls = false), 3000);
  }

  function wheelHandler(event: WheelEvent) {
    showControls();
    dispatch('ui:wheel', {
      deltaX: event.deltaX,
      deltaY: event.deltaY,
      direction: event.deltaY <= 0 ? 'up' : 'down'
    });
  }

  function mousemoveHandler(event: MouseEvent) {
    showControls();
    dispatch('ui:mousemove', event);
  }

  function mousedownHandler(event: MouseEvent) {
    dispatch('ui:mousedown', event);
  }

  function touchstartHandler(event: TouchEvent) {
    dispatch('ui:touchstart', event);
  }

  function pipHandler(event: MouseEvent) {
    dispatch('ui:pip', event);
  }

  function addListenerHandler() {
    listeners++;
    mediaControls.addEventListener('mousemove', mousemoveHandler);
  }

  function removeListenerHandler() {
    listeners--;
    mediaControls.removeEventListener('mousemove', mousemoveHandler);
  }

  function maybeEnablePipButton(node: Element) {
    if ('pictureInPictureEnabled' in document) {
      node.classList.remove('hidden');
      node.removeAttribute('disabled');
    }
  }
</script>

<div
  style:--inline-controls-inside-margin={'6px'}
  style:--fullscreen-controls-bar-height={'75px'}
  style:--primary-glyph-color={'rgba(255, 255, 255, 0.75)'}
  style:--secondary-glyph-color={'rgba(255, 255, 255, 0.55)'}
  style:--scrubber-margin={'5px'}
  style:--inline-controls-bar-height={'31px'}
  class="media-controls-container"
  {id}
>
  <div class="visible-controls-bar" style="display: none; stroke-width: 0px" />
  {#if waiting}
    <svg
      class="center"
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      stroke="#000"
      viewBox="0 0 24 24"
      ><g
        ><circle cx="12" cy="12" r="9.5" fill="none" stroke-width="1" stroke-linecap="round"
          ><animate
            attributeName="stroke-dasharray"
            dur="1.5s"
            calcMode="spline"
            values="0 150;42 150;42 150;42 150"
            keyTimes="0;0.475;0.95;1"
            keySplines="0.42,0,0.58,1;0.42,0,0.58,1;0.42,0,0.58,1"
            repeatCount="indefinite"
          /><animate
            attributeName="stroke-dashoffset"
            dur="1.5s"
            calcMode="spline"
            values="0;-16;-59;-59"
            keyTimes="0;0.475;0.95;1"
            keySplines="0.42,0,0.58,1;0.42,0,0.58,1;0.42,0,0.58,1"
            repeatCount="indefinite"
          /></circle
        ><animateTransform
          attributeName="transform"
          type="rotate"
          dur="2s"
          values="0 12 12;360 12 12"
          repeatCount="indefinite"
        /></g
      ></svg
    >
  {:else if !loadeddata}
    <button
      on:mousedown={mousedownHandler}
      class="play-pause play-pause-controllable center paused"
      aria-label="Wiedergeben"
      style="position: absolute; padding: 0; left: 0px; width: 11px; height: 13px;"
    >
      <div class="background-tint">
        <div class="blur" />
        <div class="tint" />
      </div>
      <picture
        style="
          width: 11px;
          height: 13px;
          mask-image: url('data:image/svg+xml;base64,'); width: 11px; height: 13px;
          -webkit-mask-image: url('data:image/svg+xml;base64,'); width: 11px; height: 13px;
        "
      />
    </button>
  {/if}
  {#if loadeddata}
    <div
      class="media-controls inline mac uses-ltr-user-interface-layout-direction play-pause-controllable"
      style="width: {width}px; height: {height}px"
      class:faded={!controls}
      bind:this={mediaControls}
      on:wheel={wheelHandler}
      on:touchstart={touchstartHandler}
      on:mousedown={mousedownHandler}
      on:mouseenter={addListenerHandler}
      on:mouseleave={removeListenerHandler}
      on:mouseleave
      on:mouseenter
      on:keydown
    >
      <div
        role="group"
        class="controls-bar top-left"
        style="width: 82px"
        on:mouseenter={removeListenerHandler}
        on:mouseleave={addListenerHandler}
      >
        <div class="background-tint">
          <div class="blur" />
          <div class="tint" />
        </div>
        <div class="buttons-container" style="width: 82px">
          <button
            disabled={!duration}
            on:click={() => dispatch('fullscreen')}
            class="fullscreen bar"
            aria-label={document.fullscreenEnabled
              ? $_('text.fullscreen-off')
              : $_('text.fullscreen-on')}
            style="width: 15px; height: 15px; left: 16px"
          >
            <picture
              style="
            width: 15px;
            height: 15px;
            mask-image: url('data:image/svg+xml;base64,');
            -webkit-mask-image: url('data:image/svg+xml;base64,');
          "
            /></button
          ><button
            use:maybeEnablePipButton
            on:click={pipHandler}
            class="pip bar"
            aria-label={$_('text.start-pip')}
            style="width: 19px; height: 13px; left: 47px"
          >
            <picture
              style="
            width: 19px;
            height: 13px;
            mask-image: url('data:image/svg+xml;base64,');
            -webkit-mask-image: url('data:image/svg+xml;base64,');
          "
            />
          </button>
        </div>
      </div>
      <div
        role="group"
        class="controls-bar bottom"
        aria-label="Videosteuerelemente"
        style="width: {width - 2 * 6}px; top: {height - 40}px"
      >
        <div class="background-tint">
          <div class="blur" />
          <div class="tint" />
        </div>
        <div class="buttons-container left" style="width: 101px">
          <button
            on:click={() => dispatch('rwd', 15)}
            class="skip-back bar"
            aria-label={$_('text.rewind-15-seconds')}
            style="width: 13px; height: 15px; left: 16px"
          >
            <picture
              style="
            width: 13px;
            height: 15px;
            mask-image: url('data:image/svg+xml;base64,');
            -webkit-mask-image: url('data:image/svg+xml;base64,');
          "
            /></button
          ><button
            on:click={() => dispatch('play-pause')}
            class="play-pause bar"
            class:paused
            aria-label={paused ? $_('text.play') : $_('text.pause')}
            style="width: 11px; height: 13px; left: 45px"
          >
            <picture
              style="
            width: 11px;
            height: 13px;
            opacity: 1;
            mask-image: url({paused ? dataimages.play : dataimages.pause});
            -webkit-mask-image: url({paused ? dataimages.play : dataimages.pause});"
            /></button
          ><button
            on:click={() => dispatch('fwd', 15)}
            class="skip-forward bar"
            aria-label={$_('text.foreward-15-seconds')}
            style="width: 13px; height: 15px; left: 72px"
          >
            <picture
              style="
            width: 13px;
            height: 15px;
            mask-image: url('data:image/svg+xml;base64,');
            -webkit-mask-image: url('data:image/svg+xml;base64,');
          "
            />
          </button>
        </div>
        <div
          class="time-control"
          style="left: 101px; width: {width - 101 - 86}px"
          on:keydown
          on:click|stopPropagation
        >
          <div
            class="time-label"
            aria-label={$_('text.time-elapsed', { values: { time: display(time) } })}
            style="width: 34px"
          >
            {format(time)}
          </div>
          <div
            class="slider default scrubber"
            style="height: 16px; left: 45px; width: calc(100% - {86}px);"
          >
            <div class="appearance">
              <div class="fill">
                <div class="primary" style="left: 0; width: {percentageTime}%" />
                <div class="track" style="left: 0px" />
                <div class="secondary" style="left: 0; width: {percentageBuffer}%" />
              </div>
              <div
                class="knob bar"
                style="left: {percentageTime}%;"
                on:click={mousedownHandler}
                on:keydown
              />
            </div>
            <input
              type="range"
              min="0"
              max={duration}
              step="1"
              bind:value={time}
              aria-valuetext={display(time)}
            />
          </div>
          <div
            class="time-label"
            aria-label={$_('text.remaining', { values: { time: display(duration - time) } })}
            style="width: 34px; left: calc(100% - {34}px);"
          >
            {format(duration - time, '-')}
          </div>
        </div>
        <div class="buttons-container right" style="width: {86}px; right: 0px">
          <button
            class="mute bar"
            aria-label={$mute ? $_('text.mute') : $_('text.loud')}
            on:click={() => ($mute = !$mute)}
            style="width: 22px; height: 15px; left: 36px"
          >
            <picture
              style="
            width: 22px;
            height: 15px;
            mask-image: url({$mute ? dataimages.mute : dataimages.loud});
            -webkit-mask-image: url({$mute ? dataimages.mute : dataimages.loud});
          "
            /></button
          ><button
            class="airplay bar"
            aria-label="AirPlay"
            style="width: 16px; height: 13px; left: 0px; display: none"
          >
            <picture
              style="
            width: 16px;
            height: 13px;
            mask-image: url('data:image/svg+xml;base64,');
            -webkit-mask-image: url('data:image/svg+xml;base64,');
          "
            /></button
          ><button
            class="tracks bar"
            aria-label="Medienauswahl"
            style="width: 16px; height: 13px; left: 54px; display: none"
          >
            <picture
              style="
            width: 16px;
            height: 13px;
            mask-image: url('data:image/svg+xml;base64,');
            -webkit-mask-image: url('data:image/svg+xml;base64,');
          "
            /></button
          ><button
            class="overflow bar"
            aria-label="Mehr&nbsp;…"
            style="width: 16px; height: 13px; left: 54px; display: none"
          >
            <picture
              style="
            width: 16px;
            height: 13px;
            mask-image: url('data:image/svg+xml;base64,');
            -webkit-mask-image: url('data:image/svg+xml;base64,');
          "
            />
          </button>
        </div>
        <div class="volume-slider-container" style="left: 1016px; top: 920px; display: none;">
          <div class="background-tint">
            <div class="blur" />
            <div class="tint" />
          </div>
          <div class="slider default volume" style="height: 16px; width: 60px;">
            <div class="appearance">
              <div class="fill">
                <div class="primary" style="width: 55.5px;" />
                <div class="track" style="left: 55.5px;" />
                <div class="secondary" style="left: 55.5px; right: 100%;" />
              </div>
              <div class="knob circle" style="left: 55.5px;" />
            </div>
            <input type="range" min="0" max="1" step="0.001" />
          </div>
        </div>
      </div>
    </div>
  {/if}
</div>

